import Immutable from 'immutable';

/**
 * Describes a field on an Action and how this field
 * should be sent to Siren API as part of that Action.
 *
 * @param {String} options.name:  ''     [description]
 * @param {String} options.title: null   [description]
 * @param {String} options.type:  'text' [description]
 * @param {Object} options.value: null   Default value to be sent on the field
 */
class ActionField extends Immutable.Record({
	name: '',
	title: null,
	type: 'text',
	value: null
}) {
	/**
	 * Parses a JSON representation of a field.
	 *
	 * @param {Object} json The Object representation of the ActionField.
	 * @return {ActionField} parsed structure which represents a field on an action.
	 */
	static fromJson(json) {
		if (!json.name) {
			throw new Error('name is required on an action field');
		}

		return empty.withMutations(map => {
			map.set('name', json.name || map.name);
			map.set('title', json.title || map.title);
			map.set('type', json.type || map.type);
			map.set('value', json.value || map.value);
		});
	}

	/**
	 * Default empty state for a field.
	 *
	 * @return {ActionField} Default state
	 */
	static get empty() {
		return empty;
	}
}

const empty = new ActionField();

export default ActionField;

import Immutable from 'immutable';
import superagent from 'superagent';
import bluebird from 'bluebird';
import Uri from 'urijs';

const request = require('superagent-promise')(superagent, bluebird);

let globalHeaders = new Immutable.Map();

/**
 * HTTP client used by the Super-Siren library.  Library utilized [superaget](https://github.com/visionmedia/superagent)
 * for all requests.
 */
class Client {
	/**
	 * Creates a superagent HTTP get operation
	 *
	 * @param {String} href The URL to perform an HTTP get against.
	 * @return {superagent-promise} superagent get request
	 */
	static get(href) {
		let req = request.get(href);
		addHeaders(req);

		return req;
	}

	static put(href) {
		let req = request.put(href);
		addHeaders(req);

		return req;
	}

	static post(href) {
		let req = request.post(href);
		addHeaders(req);

		return req;
	}

	static del(href) {
		let req = request.del(href);
		addHeaders(req);

		return req;
	}

	static action(method, href) {
		method = (method || 'get').toLowerCase();

		if (method === 'delete') {
			method = 'del';
		}

		let req = request[method](href);
		addHeaders(req);

		return req;
	}

	/**
	 * Registers a content type parser with all client instances.
	 *
	 * @param {String} contentType     content-type which should be parsed with the provided function
	 *                                 whenever a response is received with this content type.
	 * @param {Function} parseFunction Function to call in order to return response body when response
	 *                                 is encoded with the provided contentType.
	 * @returns {undefined}
	 */
	static addParser(contentType, parseFunction) {
		superagent.parse[contentType] = (res, done) => {
			//if res is a string, that means we're likely in a browser environment
			//and have only been provided the string to parse.
			if (typeof(res) === 'string') {
				return parseFunction(res);
			}

			//if we made it this far it's likely Node and have to do more work to parse

			//TODO: cannot assume http
			let baseUrl = null;

			if (res.req && res.req._headers && res.req._headers.host) {
				baseUrl = new Uri({protocol: 'http', hostname: res.req._headers.host}).toString();
				baseUrl = new Uri(res.req.path).absoluteTo(baseUrl).toString();
			}

			res.text = '';
			res.setEncoding('utf8');
			res.on('data', chunk => {res.text += chunk;});
			res.on('end', () => {
				let err = null;
				let body = null;

				try {
					let text = res.text && res.text.replace(/^\s*|\s*$/g, '');
					body = text && parseFunction(text, baseUrl);
				}
				catch(e) {
					err = e;
				}
				finally {
					done(err, body);
				}
			});
		};
	}

	/**
	 * Returns a Map of current global headers that are added
	 * for all clients.
	 *
	 * @return {Immutable.Map} Immutable map of all currently registered headers
	 */
	static get globalHeaders() {
		return globalHeaders;
	}

	/**
	 * Registers a global header to be used by all client instances.
	 *
	 * @param {String} header The header attribute to set
	 * @param {String} value The header value to set
	 * @returns {undefined}
	 */
	static addHeader(header, value) {
		globalHeaders = globalHeaders.set(header, value);
	}

	/**
	 * Remves a previously registered global header.
	 *
	 * @param  {String} header The header attribute to remove
	 * @returns {undefined}
	 */
	static removeHeader(header) {
		globalHeaders = globalHeaders.delete(header);
	}
}

function addHeaders(req) {
	globalHeaders.forEach((value, key) => {
		req.set(key, value);
	});
}

export default Client;

import Immutable from 'immutable';
import _ from 'lodash';
import SirenHelpers from './SirenHelpers';

/**
 * Represents an sub-entity of an entity which is represented
 * as a URL to the actual entity instance.
 */
class LinkedSubEntity extends Immutable.Record({
	rels: new Immutable.Set(),
	classes: new Immutable.Set(),
	href: null
}) {

	/**
	 * Parses the provided json instance and returns
	 * the LinkedSubEntity representation of that json
	 *
	 * @param  {Object} json Object representation of the linked sub-entity
	 * @param  {String} [baseUrl=null] Optional baseUrl to use if the href is a relative URL
	 * @return {LinkedSubEntity}      Parsed representation of the linked sub-entity.
	 */
	static fromJson(json, baseUrl=null) {
		if (!json.rel || !_.isArray(json.rel) || json.rel.length === 0) {
			throw new Error('A rel array with at least one rel is required to create a linked sub entity');
		}

		if (!json.href) {
			throw new Error('a href is required for a linked sub entity');
		}

		return empty.withMutations(map => {
			map.set('rels', new Immutable.Set(json.rel));
			map.set('href', SirenHelpers.processUrl(json.href || map.href, baseUrl));
			map.set('classes', new Immutable.Set(json.class || []));
		});
	}

	/**
	 * Default empty instance of a LinkedSubEntity
	 * @return {LinkedSubEntity} Default empty state
	 */
	static get empty() {
		return empty;
	}
}

const empty = new LinkedSubEntity();

export default LinkedSubEntity;

import Promise from 'bluebird';
import Immutable from 'immutable';
import LinkedSubEntity from './LinkedSubEntity';
import SirenLink from './SirenLink';
import SirenAction from './SirenAction';
import _ from 'lodash';
import Client from './Client';
import SirenHelpers from './SirenHelpers';

Client.addParser('application/vnd.siren+json', (text, requestUrl) => Siren.fromJson(JSON.parse(text), requestUrl));

Client.addHeader('Accept', 'application/vnd.siren+json');

const client = new Client();

/**
 * @class Siren
 * Immutable Siren entity.  This is the main entrypoint for all Siren operations.
 */
class Siren extends Immutable.Record({
	classes: Immutable.Set(),
	properties: Immutable.Map(),
	entities: Immutable.List(),
	actions: Immutable.Map(),
	links: Immutable.List()
}) {
	constructor(args) {
		if (!args && Siren.empty) {
			return Siren.empty;
		}
		else {
			super(args);
		}
	}

	/**
	 * Finds the @see {@link SirenAction} referenced by the provided rel.
	 *
	 * @param  {String} name The name of the action to find.
	 * @return {SirenAction} SirenAction matching the requested name.  null if none is found.
	 */
	findActionByName(name) {
		return this.actions.get(name) || null;
	}

	/**
	 * Finds the first @See {@link SirenLink} referenced by the provided rel.
	 *
	 * @param  {String} rel The relation to this Siren entity for the requested link.
	 * @return {SirenLink}     SirenLink matching the requested rel.  null if none is found.
	 */
	findLinkByRel(rel) {
		return this.links.filter(link => link.rels.contains(rel)).first() || null;
	}

	/**
	 * Finds the @See {@link EmbeddedSubEntity}|{@link LinkedSubEntity} entities referenced by the provided rel.
	 *
	 * @param  {String} rel The relation to this Siren entity for the requested sub-entity.
	 * @return {Immutable.List}  List of sub-entities matching the requested rel.
	 */
	findEntitiesByRel(rel) {
		return this.entities.filter(item => item.rels.contains(rel));
	}

	/**
	 * Returns the sub-entities on this Siren object which are embedded sub-entities.
	 *
	 * @return {Immutable.List}     List of embedded sub-entities.
	 */
	get embeddedEntities() {
		return this.entities
			.filter(item => item instanceof EmbeddedSubEntity)
			.toSet();
	}

	/**
	 * Returns the sub-entities on this Siren object which are embedded sub-entities.
	 *
	 * @param  {String} rel 	Only entities with a relation to the parent siren matching this should be returned.
	 * @return {Immutable.List}  List of embedded sub-entities which match thes provided rel.
	 */
	embeddedEntitiesByRel(rel) {
		return this.embeddedEntities
			.filter(item => item.rels.contains(rel));
	}

	/**
	 * Returns the sub-entities on the Siren object which are linked sub-entities.
	 *
	 * @return {Immutable.List}     List of linked sub-entities on this Siren object.
	 */
	get linkedEntities() {
		return this.entities
			.filter(item => item instanceof LinkedSubEntity)
			.toSet();
	}

	/**
	 * Returns the set of linked sub-entities on the Siren object which match the requested rel.
	 *
	 * @param  {String} rel     Only entities with this relation to the parent siren should be returned.
	 * @return {Immutable.List}  List of linked sub-entities which match the provided rel.
	 */
	linkedEntitiesByRel(rel) {
		return this.linkedEntities
			.filter(item => item.rels.contains(rel));
	}

	/**
	 * Returns the self link for this entity
	 *
	 * @return {SirenLink} link represented by the self rel, null if no self link is found.
	 */
	get selfLink() {
		return this.findLinkByRel('self');
	}

	//TODO: add a validate method and use it while parsing
	/**
	 * Parses a JSON representation of a Siren entity
	 * and returns the Siren representation.
	 * @param {Object} [obj] The JSON object to be parsed as Siren
	 * @param {String} [baseUrl=null] Optional base URL to use for relative URL parsing
	 * @return {Siren} Parsed Siren entity
	 */
	static fromJson(obj, baseUrl = null) {
		return Siren.empty.withMutations(map => {
			map.set('classes', map.classes.union(obj.class ? Immutable.fromJS(obj.class) : new Immutable.List()));

			for (let key in obj.properties) {
				map.set('properties', map.properties.set(key, obj.properties[key]));
			}

			map.set('links',
				new Immutable.List(
					_.map(obj.links || [], (item) => new SirenLink(item.rel, SirenHelpers.processUrl(item.href, baseUrl), item.class))
				)
			);

			map.set('actions',
				new Immutable.Map(
					_.map(obj.actions || [], (item) => SirenAction.fromJson(item, baseUrl))
					.map(action => [action.name, action])
				)
			);

			map.set('entities',
				new Immutable.List(
					_.map(obj.entities || [], (item) => item.href ? LinkedSubEntity.fromJson(item, baseUrl) : EmbeddedSubEntity.fromJson(item, baseUrl))
				)
			);
		});
	}

	/**
	 * Returns an empty siren representation.  This Siren entity
	 * contains no afforances.
	 * @return {Siren} Empty siren structure
	 */
	static get empty() {
		return emptySiren;
	}

	/**
	 * Returns a Superagent Promise instance which will perform an HTTP Get against
	 * the provided href returning the response as a SuperAgent response.
	 * If the response is Siren ('application/vnd.siren+json'),
	 * then the body should be a Siren instance.
	 *
	 * @param {String} href The URL to perform an HTTP get against
	 * @return {superagent-promise} Superagent Promise Object
	 */
	static get(href) {
		return Client.get(href);
	}
}

const emptySiren = new Siren();

/**
 * @class EmbeddedSubEntity
 * Entity which has been embedded within a parent Siren instance.
 *
 * @param {Array} options.rels: new Immutable.Set() array of strings to identify how this
 *                              embedded entity is related to it's parent.
 * @param {Object} options.entity: Siren.empty embedded entity instance
 */
class EmbeddedSubEntity extends Immutable.Record({
	rels: new Immutable.Set(),
	entity: Siren.empty
}) {
	/**
	 * Parses the provided JSON representation of the Siren sub entity
	 * into an instance of an EmbeddedSubEntity.
	 *
	 * @param  {Object} json           The JSON representation of a siren embedded sub entity
	 * @param  {String} [baseUrl=null] Optional base URL to use in case URLs are relative URLs
	 * @return {EmbeddedSubEntity}     The representation of the parsed JSON
	 */
	static fromJson(json, baseUrl=null) {
		if (!json.rel || !_.isArray(json.rel) || json.rel.length === 0) {
			throw new Error('A rel array is required to parse an embedded sub entity');
		}

		return EmbeddedSubEntity.empty.withMutations(map => {
			map.set('rels', new Immutable.Set(json.rel));
			map.set('entity', Siren.fromJson(json, baseUrl));
		});
	}

	/**
	 * Returns the default empty instance of an EmbeddedSubEntity.
	 *
	 * @return {EmbeddedSubEntity} default embedded sub entity
	 */
	static get empty() {
		return emptyEmbedded;
	}
}

const emptyEmbedded = new EmbeddedSubEntity();

Siren.Link = SirenLink;
Siren.Action = SirenAction;
Siren.LinkedSubEntity = LinkedSubEntity;
Siren.EmbeddedSubEntity = EmbeddedSubEntity;
Siren.Client = Client;

Siren.Helper = SirenHelpers;

export default Siren;

import _ from 'lodash';
import Immutable from 'immutable';
import ActionField from './ActionField';
import Client from './Client';
import SirenHelpers from './SirenHelpers';

/**
 * Representation of a Siren action, which allows a state within a Siren Hypermedia API
 * to transition to a new state.
 *
 * @param {String} options.name:   null                                [description]
 * @param {String} options.title:  null                                [description]
 * @param {String} options.method: 'GET'                               [description]
 * @param {String} options.href:   null                                [description]
 * @param {String} options.type:   'application/x-www-form-urlencoded' [description]
 * @param {Immutable.Map} options.fields: new Immutable.Map()          [description]
 */
class SirenAction extends Immutable.Record({
	name: null,
	title: null,
	method: 'GET',
	href: null,
	classes: new Immutable.Set(),
	type: 'application/x-www-form-urlencoded',
	fields: new Immutable.Map()
}) {
	/**
	 * Performs the action specified by this Action.
	 *
	 * @param  {Object=} data                          Query params sent as part of this action
	 * @return {superagent-promise} Superagent Promise HTTP request to perform this action
	 */
	perform(data) {
		data = data || {};

		const req = Client.action(this.method, this.href);

		if (this.type && this.type !== 'multipart/form-data') {
			//superagent needs to set it's own type for multipart/form-data content
			req.type(this.type);
		}

		const payload = {};

		this.fields.forEach(f => { if(f.value) payload[f.name] = f.value });
		Immutable.fromJS(data).forEach((value, key) => payload[key] = value);

		if (this.method.toLowerCase() === 'get') {
			req.query(payload);
		}
		else {
			if (this.type === 'multipart/form-data') {
				const formData = new FormData();

				for (let key in payload) {
					formData.append(key, payload[key]);
				}

				req.send(formData);
			}
			else {
				req.send(payload);
			}
		}

		return req;
	}

	/**
	 * Creates a SirenAction instance based on the provided
	 * JSON object structure.
	 *
	 * @param  {Object} json      Object which matches the Siren JSON structure.
	 * @param  {String} [baseUrl] optional url to use as the base URL for all parsed URLs
	 * @return {SirenAction}      Result of parsing the provided JSON object.
	 */
	static fromJson(json, baseUrl) {
		if (!json.name) {
			throw new Error('"name" is requires on an action');
		}

		return empty.withMutations(map => {
			map.set('name', json.name || map.name);
			map.set('title', json.title || map.title);
			map.set('method', json.method || map.method);
			map.set('href', SirenHelpers.processUrl(json.href || map.href, baseUrl));
			map.set('classes', new Immutable.Set(json.class || []));
			map.set('type', json.type || map.type);
			map.set('fields', new Immutable.Map(_
				.map(json.fields || [], f => ActionField.fromJson(f))
				.map(af => [af.name, af]))
			);
		});
	}

	/**
	 * Default state for a SirenAction.  This has the defaults set and nothing
	 * else.
	 *
	 * @return {SirenAction} Siren Action which is created as a result of parsing
	 *                             the provided JSON.
	 */
	static get empty() {
		return empty;
	}
}

const empty = new SirenAction();

export default SirenAction;

import Client from './Client';
import Uri from 'urijs';

/**
 * Follows the Siren model's link by the provided rel.
 *
 * @param  {String} rel  how the requested link is related to the result siren we are following.
 * @return {Promise}     Promise which resolves to a superagent response
 */
function follow(rel) {
	return (res) => {
		const link = res.body.findLinkByRel(rel)
			|| res.body.linkedEntitiesByRel(rel).map(item => item.href).first()
			|| res.body.embeddedEntitiesByRel(rel).map(item => item.entity.selfLink).first();

		if (!link) {
			throw new Error('No link found for provided rel', {rel});
		}

		return typeof link === 'string' ? Siren.get(link) : link.follow();
	};
}

/**
 * Performs the action identified by the provided actionName using the provided data.
 *
 * @param  {String} name       The name that identifies the action to take
 * @param  {Object=} data      The data to send on the action
 * @return {Promise}           Promise which resolves to a superagent response
 */
function performAction(name, data) {
	return (res) => {
		const action = res.body.findActionByName(name);

		if (!action) {
			throw new Error('No action found for the provided name', {name});
		}

		return action.perform(data);
	};
}

/**
 * Processes the provided url and computes the absolute URL provided
 * the url and optional baseUrl.
 *
 * @param  {String} url          The URL to process
 * @param  {String} [baseUrl]    Optional base URL to compute the absolute URL relative to.
 * @return {String}              Absolute URL
 */
function processUrl(url, baseUrl) {
	if (url && url.length > 0) {
		let uri = new Uri(url);

		if (baseUrl) {
			uri = uri.absoluteTo(baseUrl);
		}

		return uri.toString();
	}

	return null;
}

export default {
	follow,
	performAction,
	processUrl
};

import Immutable from 'immutable';
import Client from './Client';

/**
 * Siren link used to describe navigation through a siren entity graph.
 *
 * @property {Immutable.Set} rels - set of all rels which describe this link.
 * @property {String} href URL - that this link refers to.
 */
class SirenLink extends Immutable.Record({
	/**
	 * @member {Immutable.Set}
	 */
	rels: new Immutable.Set(),
	classes: new Immutable.Set(),
	href: null
}) {
	/**
	 * Constructs a new SirenLink based on the provided rels and href values.
	 *
	 * @param  {Array.<String>} rels - array of rel values for this link
	 * @param  {String} href - URL that this link refers to.
	 * @param   {Array.<String>} classes - Array of optional class names
	 *
	 * @return {SirenLink} Constructed SirenLink instance.
	 */
	constructor(rels, href, classes = []) {
		super({rels: new Immutable.Set(rels), href: href, classes: new Immutable.Set(classes)});
	}

	/**
	 * Perhaps an HTTP Get against the href represented by this Link.
	 *
	 * @return {superagent-promise} superagent request as a with a promise wrapper
	 */
	follow() {
		return Client.get(this.href);
	}
}

export default SirenLink;
